INSERT INTO Product (name, description, price, stock_left) VALUES ('Canon EF 40 mm f/2.8 STM', 'Pienikokoinen normaaliobjektiivi, joka kulkee kätevästi mukana matkalla. Kuvakulma sopii mainiosti yleiskuvaukseen täyden kennon kameroilla ja toimii näppäränä pikkutelenä APS-C-kennoisissa vaikkapa potrettikuvauksessa. Hyvä valovoima mahdollistaa kuvaamisen hämärässä ja hiljainen STM-askelmoottori tarkentaa pehmeästi videokuvauksen aikana ominaisuutta tukevilla kameroilla.', 244.90, 3);
