INSERT INTO Product (name, description, price, stock_left) VALUES ('Samyang 14mm f/2.8 IF ED UMC Aspherical', 'Edullinen laajakulmaobjektiivi, jossa on käytetty väripoikkeamaa vähentäviä ED-elementtejä yhdessä asfääristen linssien kanssa. Näin saavutettu kuvanlaatu on vertailukelpoinen huomattavasti kalliimpienkin objektiivien kanssa. Soveltuu käytettäväksi myös täyden kennon järjestelmäkameroissa.', 429.90, 1);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Olympus M.ZUIKO DIGITAL 25 mm f/1,8 Micro Four Thirds', 'Erinomainen valovoimainen normaaliobjektiivi, joka sopii hyvin monenlaiseen kuvaamiseen. Suuren valovoiman ja Olympuksen PEN- tai OM-D -kameran runkovakaajan ansiosta voit ottaa teräviä kuvia käsivaralta myös hyvin vähäisessä valossa.', 359.90, 2);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Olympus M.ZUIKO DIGITAL 14-42 mm 1:3.5-5.6 II R', 'Erittäin pienikokoinen zoomobjektiivi, joka tarjoaa 3x zoomin. Hiljainen ja nopea tarkennus sopivat erinomaisesti sekä valokuviin että videoon.', 329.90, 4);
