INSERT INTO Product (name, description, price, stock_left) VALUES ('Sony FE 24-105 mm F4 G OSS', 'G Lens -objektiivin polttovälialue on 24–105 mm ja F4-aukko on vakio koko zoomausalueella. Pieni ja kevyt objektiivi sopii mukana kuljetettaviin E-tyypin runkoihin ja sen optinen suorituskyky on loistava. Nopean ja tarkan automaattitarkennuksen ja luotettavuuden ansiosta laadukas objektiivi on hyvin monikäyttöinen.', 1369.90, 0);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Canon EF-S 17-55mm f/2.8 IS USM', 'Valovoimainen kuvanvakaimella varustettu objektiivi, joka tarkentaa nopeasti ja äänettömästi. Jos käytössäsi on esim. useiden Canon digijärjestelmäkameroiden mukana tuleva EF-S 18-55mm -objektiivi ja tunnet kaipaavasi terävämpiä kuvia tai mahdollisuuden kuvata hieman heikomassakin valossa, niin tämä objektiivi tarjoaa selkeän parannuksen aikaisempaan.', 849.90, 1);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sigma 24-70 mm F2.8 DG OS HSM Art', 'Laadukas työhevonen viimeisimmille korkean pikselimäärän full frame -kameroille. Korkealaatuinen metallirunko ja huippuluokan optiikka tekevät tästä objektiivista varman valinnan laatutietoiselle käyttäjälle. Kolme SLD-elementtiä ja neljä asfääristä elementtiä. Japanissa valmistettu.', 1429.90, 3);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sony Carl Zeiss Sonnar T* FE 55 mm F1,8 ZA', 'Ammattilaatuinen normaaliobjektiivi, jonka linsseissä on käytetty Carls Zeiss:in T* -pinnoitteita vähentämään sisäisiä heijastuksia ja näin lisäämään kontrastia. Suunniteltu erityisesti täyden kennon A7 ja A7r -kameroille.', 899.90, 1);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Canon EF 24-70 mm 2.8 L II USM', 'Ammattitason vakiozoom-objektiivi, joka tuottaa asfääristen linssielementtien sekä UD- ja Super UD -elementtien ansiosta erinomaisen kuvanlaadun koko zoomausalueella. EF 24-70 mm f/2.8L II USM on tukevarakenteinen ja päivittäistä ammattikäyttöä varten suunniteltu. Sääsuojaus estää pölyn ja kosteuden pääsyn objektiiviin, ja fluoripinnoite helpottaa etu- ja takaelementtien puhdistusta. Täydellinen lisävaruste kaikkiin täyden kennokoon EOS-kameroihin.', 2299.90, 0);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sigma 24-105 mm F4 DG OS HSM | A', 'Täyden kennon rungoille suunniteltu yleiskäyttöinen zoomobjektiivi, jonka jykevä rakenne vakuuttaa ammattilaisenkin. Laaja polttovälialue, asiallinen valovoima ja kuvanvakain tekevät tästä erittäin käyttökelpoisen työkalun tilanteisiin, joissa objektiivin jatkuva vaihtaminen ei ole vaihtoehto. Kuten muutkin Sigman uudet Art-sarjan objektiivit, tämänkin kuvanlaatu on sanalla sanoen huikaiseva - etenkin jos otetaan huomioon äärimmäisen kilpailukykyinen hinta. Tarkennuksen hienosäätö eri polttoväleille onnistuu kotikonstein Sigman USB-telakan avulla (lisävaruste).', 745.90, 3);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sigma 14 mm F1.8 DG HSM Art', 'Maailman ensimmäinen 14 mm objektiivi korkealla f1.8-valovoimalla on suunniteltu uusia korkean pikselimäärän full frame -kameroita varten. Suuri asfäärinen elementti korjaa vääristymiä ja kolme FLD sekä neljä SDL -elementtiä korjaavat väripoikkeamia ja comaa.', 1599.90, 0);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sony E PZ 18-105 mm F4 G OSS', 'Laadukas yleiskäyttöinen zoom-objektiivi moottoroidulla zoomilla, jonka avulla saat videokuvaan sulavat zoomaustehosteet, jotka on vaikea saavuttaa manuaalisesti. Hiljaisen käyttöäänen ansiosta zoomaus ei kuulu videolla, eikä säikäytä kuvauskohteita. Asfääriset linssielementit minimoivat vääristymät, ja Extra-Low Dispersion (ED) -lasielementit varmistavat ylivoimaisen kontrastin, tarkkuuden ja värientoiston.', 599.90, 1);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sony FE 24 - 70mm F2,8 GM', 'Parasta mahdollista normaalizoomia A7-sarjan kameraansa hakevan ei tarvitse kauemmas katsella - se on tässä! Kiinteä 2.8-valovoima läpi zoomalueen ja erinomainen kontrasti sekä terävyys ovat ominaisuuksia, joita arvostaa niin ammattilainen kuin vaativa harrastajakin.', 2399.90, 1);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sigma 24 mm F1,4 DG HSM | Art', 'Tyypillisessä 24 mm f/1.4 objektiivissa on 12-13 linssielementtiä - tässä Sigmassa elementtejä on 15 ja niistä yli puolet on kalliita erikoiselementtejä. Ja se näkyy kuvanlaadussa - kyseessä on varovasti sanoen luokkansa paras objektiivi. Sigman Art-sarjan kiinteäpolttoväliset objektiivit ovat nyt nostaneet riman sille tasolle, että myötähäpeän tunteelta perinteisiä kameravalmistajia kohtaan ei voi välttyä, etenkin jos objektiiveja erehtyy vertaamaan samalla viivalla ilman ennakkoluuloja. Erinomainen valinta etenkin maisema- ja tähtikuvaukseen.', 699.90, 6);
INSERT INTO Product (name, description, price, stock_left) VALUES ('Sigma 16 mm F1.4 DC DN Contemporary', 'Valovoimainen laajakulmaobjektiivi Sonyn peilittömille kameroille. Suunniteltu APS-C-kennoisille kameroille, mutta croppitilassa tätä voi mainiosti käyttää myös E-bajonetin Full Frame -kameroissa.', 399.90, 4);
